package com.abdurashidov.mvp_pattern.presenterinterface Contract {    interface View{        fun showProgress()        fun hideProgress()        fun setString(string: String?)    }    interface Model{        interface onFinishedListener{            fun onFinished(string: String?)        }        fun getNextCourse(onFinishedListener: onFinishedListener?)    }    interface Presenter{        fun onButtonClick()        fun onDestroy()    }}